import React, { useState } from "react";

function App() {
  const [headingText, headingState] = useState("Hello");
  const [inputText, setInputText] = useState(""); // State untuk menyimpan nilai input

  function handleClick() {
    headingState("Submitted");
  }

  function handleMouseOver() {
    setMouseOver(true);
  }

  function handleMouseOut() {
    setMouseOver(false);
  }

  function handleInputChange(event) {
    // Fungsi untuk menangani perubahan input
    setInputText(event.target.value); // Mengubah nilai state inputText sesuai dengan nilai input
  }

  return (
    <div className="container">
      <h1>{headingText}</h1>
      <input
        type="text"
        placeholder="What's your name?"
        value={inputText} // Nilai input disetel ke state inputText
        onChange={handleInputChange} // Menangani perubahan nilai input
      />
      <button
        style={{
          backgroundColor: isMouseOver ? "black" : "white",
          marginRight: "10px" // Contoh penggunaan inline CSS untuk menambahkan margin
        }}
        onClick={handleClick}
        onMouseOver={handleMouseOver}
        onMouseOut={handleMouseOut}
      >
        Submit
      </button>

      <button style={{ backgroundColor: "red" }}>Delete</button>

    </div>
  );
}

export default App;
